import Caja from '../Pregun_Views/CajaWord.vue';
export default {
  props: ['pregunta'],
  components: {
    Caja
  },

  data() {
    return {
      frase: "",
      respuestaCorrecta: this.pregunta.respuesta,
      respuestaEstudiante: "",
      mostrarMensaje: false,
      mensajeValidacion: "",
      mensajeClase: ""
    };
  },

  mounted() {
    const patron = /:(.*?):/g;
    const nuevoTexto = this.pregunta.enunciado.replace(patron, `<span class="caja"><span class="palabra"></span></span>`);
    this.frase = nuevoTexto;
  },

  methods: {
    dragStart(palabra) {
      this.respuestaEstudiante = palabra;
    },

    allowDrop(ev) {
      ev.preventDefault();
    },

    drop(ev) {
      ev.preventDefault();
      const palabra = ev.dataTransfer.getData('text/plain');
      this.frase = reemplazarPalabras(this.frase, palabra);
    },

    validarFrase() {
      // Convertir la respuesta del estudiante en un array de palabras
      const respuestaEstudianteArray = this.respuestaEstudiante.split(' '); // Convertir el enunciado de la pregunta en un array de palabras

      const enunciadoArray = this.pregunta.enunciado.split(' '); // Verificar que la respuesta tenga el mismo número de palabras que el enunciado

      if (respuestaEstudianteArray.length !== enunciadoArray.length) {
        mostrarMensaje('La respuesta es incorrecta. El número de palabras es diferente.', 'mensaje-incorrecto');
        return;
      } // Comparar las dos cadenas sin tener en cuenta el orden de las palabras


      if (respuestaEstudianteArray.join(' ') === enunciadoArray.join(' ')) {
        mostrarMensaje('¡Respuesta correcta! Has completado la frase correctamente.', 'mensaje-correcto');
      } else {
        mostrarMensaje('La respuesta es incorrecta. El orden de las palabras es incorrecto.', 'mensaje-incorrecto');
      }
    }

  }
};
