import Caja from '../Pregun_Views/CajaWord.vue';
export default {
  props: ['pregunta'],
  components: {
    Caja
  },

  data() {
    return {
      frase: "",
      respuestaCorrecta: this.pregunta.respuesta,
      respuestaEstudiante: "",
      mostrarMensaje: false,
      mensajeValidacion: "",
      mensajeClase: ""
    };
  },

  mounted() {
    const patron = /:(.*?):/g;
    const nuevoTexto = this.pregunta.enunciado.replace(patron, `<span class="caja"><span class="palabra"></span></span>`);
    this.frase = nuevoTexto;
  },

  methods: {
    dragStart(palabra) {
      this.respuestaEstudiante = palabra;
    },

    allowDrop(ev) {
      ev.preventDefault();
    },

    drop(ev) {
      ev.preventDefault();
      const palabra = ev.dataTransfer.getData('text/plain');

      if (this.frase.includes('<span class="palabra"></span>')) {
        this.frase = this.frase.replace('<span class="palabra"></span>', palabra);
      }
    },

    validarFrase() {
      // Convertir la respuesta del estudiante en un array de palabras
      const respuestaEstudianteArray = this.respuestaEstudiante.split(' '); // Convertir el enunciado de la pregunta en un array de palabras

      const enunciadoArray = this.pregunta.enunciado.split(' '); // Verificar que la respuesta tenga el mismo número de palabras que el enunciado

      if (respuestaEstudianteArray.length !== enunciadoArray.length) {
        this.mostrarMensaje = true;
        this.mensajeValidacion = 'Respuesta incorrecta. El número de palabras es diferente.';
        this.mensajeClase = 'mensaje-incorrecto';
        return;
      } // Comparar las dos cadenas


      const respuestaCorrecta = respuestaEstudianteArray.join(' ') === enunciadoArray.join(' ');

      if (respuestaCorrecta) {
        this.mostrarMensaje = true;
        this.mensajeValidacion = 'Respuesta correcta. ¡Has completado la frase correctamente!';
        this.mensajeClase = 'mensaje-correcto';
      } else {
        this.mostrarMensaje = true;
        this.mensajeValidacion = 'Respuesta incorrecta. El orden de las palabras es incorrecto.';
        this.mensajeClase = 'mensaje-incorrecto';
      }
    }

  }
};
